import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import { UserAPI } from "../../../api";
import { Image, Typography, Rate, Skeleton, Result } from "antd";
import "./ProfileCards.css";
import profile from "../../../assets/images/profile-pic.jpg";
import FilterBar from "../FilterBar/FilterBar";

const { Title } = Typography;

const ProfileCards = () => {
  const { service, emergency } = useParams();
  const navigate = useNavigate();
  const [data, setData] = React.useState([]);
  const [duplicateData, setDuplicateData] = React.useState([]);
  const [showSkeleton, setShowSkeleton] = React.useState(false);
  React.useEffect(() => {
    setShowSkeleton(true);
    UserAPI.getCareGiver(service, emergency)
      .then((res) => {
        console.log(res?.data?.data);
        setData(res?.data?.data);
        setDuplicateData(res?.data?.data);
        setShowSkeleton(false);
      })
      .catch((err) => {
        setShowSkeleton(false);
      });
  }, [service]);

  const onHandleProfile = (id) => {
    navigate(`/visitor/profileData/${id}`);
    const _id = JSON.parse(localStorage.getItem("user"))?._id;
    if (_id) {
      UserAPI.addView(_id, { id }).then((res) => {
        console.log(res?.data?.data);
      });
    }
  };

  return (
    <>
      <FilterBar data={duplicateData} setData={setData} />
      <hr />
      <br />
      <div className="profile-cards-container">
        <div className="profile-cards-main">
          {showSkeleton == false && data?.length == 0 ? (
            <Result
              status="404"
              title="No Data to Show"
              // subTitle="Sorry, the page you visited does not exist."
            />
          ) : null}
          {showSkeleton ? (
            <div className="skeleton-data">
              <Skeleton
                active
                avatar
                paragraph={{
                  rows: 4,
                }}
              />
              <hr />
              <Skeleton
                active
                avatar
                paragraph={{
                  rows: 4,
                }}
              />
              <hr />
              <Skeleton
                active
                avatar
                paragraph={{
                  rows: 4,
                }}
              />
            </div>
          ) : (
            <>
              {data?.map((item) => {
                return (
                  <>
                    <div className="profile-card">
                      <div className="profile-card-section-1">
                        <Image
                          className="profile-image"
                          width={140}
                          height={140}
                          style={{ borderRadius: "15px" }}
                          src={item?.profileImg}
                          fallback="data:image/png;base64,"
                        />
                        <div className="profile-address">{item?.address}</div>
                      </div>
                      <div
                        className="profile-card-section-2"
                        onClick={() => onHandleProfile(item?._id)}
                      >
                        <div className="address-rate">
                          <Title level={4}>{item?.name}</Title>
                          <Rate defaultValue={4} disabled />
                        </div>
                        <div className="hourly-exp-Availabilty">
                          <ul>
                            <li>{item?.experience} years exp</li>
                            <li>
                              {item?.weeklyHours[0]}hr-{item?.weeklyHours[1]}
                              hr/wk
                            </li>
                            <li>${item?.hourlyRate}/hr</li>
                            <li>In Morning</li>
                          </ul>
                        </div>
                        <div className="profile-description">
                          <p>{item?.bioTitle}</p>
                          <p>{item?.bioDescription}</p>
                        </div>
                      </div>
                    </div>
                    <hr />
                  </>
                );
              })}
            </>
          )}
        </div>
      </div>
      <br />
    </>
  );
};

export default ProfileCards;
